INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES(14, 4, '2012-12-14 11:41:33', '2012-12-14 11:41:33', 'A health savings account (HSA) is a medical savings account available to taxpayers in the United States who are enrolled in a high deductible health plan.  An HSA is owned by the individual, who deposits money, often matched by their employer, into their account.  This total is rolled over from year to year, allowing individuals to build the value over an extended period of time.  HSAs possess tax benefits in that they are not subject to federal income tax at the time of deposit, and if withdrawals are made for approved healthcare services, the withdrawal is tax free as well.\r\n\r\n[caption id="" align="alignnone" width="400"]<img title="Medical Tourism and Health Savings Accounts" alt="Medical Tourism and Health Savings Accounts | healthcareabroad.com" src="http://farm8.staticflickr.com/7191/6869772267_859961ebb2.jpg" width="400" height="400" /> An HSA is owned by the individual, who deposits money, often matched by their employer, into their account.<br />Photo by <a href="http://www.401kcalculator.org/">401(K) 2013</a>[/caption]\r\n\r\nThe benefit for people with HSAs that are interested in medical tourism is that almost all aspects of a medical tourism trip are included as approved healthcare services for procedures deemed medically necessary.  This means that your medical procedure, airfare, local travel, and accommodations while traveling are all payable, tax free, out of your HSA.\r\n\r\nSo in addition to the tremendous savings that can be found through medical tourism, the power of bundling the savings with the tax-free advantages available through an HSA make it truly a remarkable opportunity.', 'Medical Tourism and Health Savings Accounts: All Aboard!', '', 'publish', 'open', 'open', '', 'medical-tourism-and-health-savings-accounts-all-aboard', '', '', '2013-01-11 02:58:12', '2013-01-11 02:58:12', '', 0, 'http://www.healthcareabroad.com/blog/?p=14', 0, 'post', '', 0);
